mapper.update:
		UPDATE userchat
	    SET userchat_subject = #{userchat_subject}, 
	        userchat_state = #{userchat_state},
	        max_people = #{max_people}
    	WHERE userchat_idx = #{userchat_idx}

UPDATE userchat
SET notice = #{notice}
WHERE userchat_idx = #{userchat_idx}

UPDATE userchat SET userchat_state = 0
WHERE userchat_idx = #{userchat_idx} 
AND user_id = #{user_id}
