mapper.update:
UPDATE report
SET report_state = #{result, jdbcType=INTEGER}
WHERE report_idx = #{report_idx}

UPDATE user
SET user_reportno = (SELECT COUNT(reporting_id) from report where reporting_id = #{reportingId}), user_reportedcases = (SELECT COUNT(reported_id) from report where reported_id=#{reportedId} )
WHERE user_id = #{reportedId} or user_id = #{reportingId}
